import 'package:flutter/material.dart';
import 'package:multi_channel/src/core/ui/constants/colors_constants.dart';
import 'package:multi_channel/src/core/ui/widgets/home_drawer.dart';
import 'package:multi_channel/src/core/ui/widgets/shared_scaffold.dart';
import 'package:multi_channel/src/data/models/chat_client.dart';
import 'package:multi_channel/src/data/models/chat_message.dart';
import 'package:multi_channel/src/providers/theme_provider.dart';
import 'package:provider/provider.dart';

class MessageCenterPage extends StatefulWidget {
  const MessageCenterPage({super.key});

  @override
  State<MessageCenterPage> createState() => _MessageCenterPageState();
}

class _MessageCenterPageState extends State<MessageCenterPage> {
  int selectedChatIndex = 0;
  bool showLeftPanel = true;

  final List<ChatClient> clients = [
    ChatClient(
      name: 'Jo√£o da Silva',
      avatarUrl:
          'data:image/jpeg;base64,',
      lastMessage: 'O produto chegou hoje, obrigado!',
      messages: [
        ChatMessage(
            text: 'Ol√°, j√° recebeu o produto em sua resid√™ncia?',
            fromClient: false),
        ChatMessage(text: 'Ol√°, chegou hoje sim.', fromClient: true),
        ChatMessage(
            text: 'Fico feliz! Qualquer coisa fico √† disposi√ß√£o.',
            fromClient: false),
      ],
    ),
    ChatClient(
      name: 'Maria Oliveira',
      avatarUrl:
          "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ97wv_mB5KYYhTmC0PE7YwaCFK-a_C5EEHdg&s",
      lastMessage: 'Voc√™s vendem por atacado?',
      messages: [
        ChatMessage(text: 'Voc√™s vendem por atacado?', fromClient: true),
        ChatMessage(
            text: 'Sim! A partir de 10 unidades j√° consideramos.',
            fromClient: false),
      ],
    ),
    ChatClient(
      name: 'Get Start',
      avatarUrl:
          "https://static.vecteezy.com/system/resources/previews/047/656/219/non_2x/abstract-logo-design-for-any-corporate-brand-business-company-vector.jpg",
      lastMessage: 'Voc√™s vendem por atacado?',
      messages: [
        ChatMessage(
            text: 'Qual o tempo para envio da minha mercadoria?',
            fromClient: true),
        ChatMessage(text: 'Em at√© 24hs', fromClient: false),
      ],
    ),
  ];

  final TextEditingController _msgController = TextEditingController();

  void sendMessage() {
    final text = _msgController.text.trim();
    if (text.isNotEmpty) {
      setState(() {
        clients[selectedChatIndex].messages.add(
              ChatMessage(text: text, fromClient: false),
            );
        _msgController.clear();
      });
    }
  }

  @override
  Widget build(BuildContext context) {
    final themeProvider = Provider.of<ThemeProvider>(context);
    final selectedClient = clients[selectedChatIndex];

    return SharedScaffold(
        title: 'Central de Mensagens',
        drawer: HomeDrawer(
          isDarkMode: themeProvider.isDarkMode,
          onThemeChanged: themeProvider.toggleTheme,
        ),
        body: Row(
          children: [
            Container(
              width: 300,
              decoration: BoxDecoration(
                color: ColorsConstants.greyDark.withValues(alpha: 0.3),
                border: const Border(
                  right: BorderSide(color: Colors.grey, width: 0.5),
                ),
              ),
              child: ListView.separated(
                itemCount: clients.length,
                separatorBuilder: (_, __) => const Divider(height: 1),
                itemBuilder: (_, index) {
                  final client = clients[index];
                  return ListTile(
                    leading: CircleAvatar(
                      backgroundColor: Colors.grey.shade200,
                      radius: 24,
                      child: ClipOval(
                        child: Image.network(
                          client.avatarUrl,
                          width: 48,
                          height: 48,
                          fit: BoxFit.cover,
                          errorBuilder: (context, error, stackTrace) =>
                              const Icon(Icons.person, size: 24),
                        ),
                      ),
                    ),
                    title: Text(client.name),
                    subtitle: Text(
                      client.lastMessage,
                      maxLines: 1,
                      overflow: TextOverflow.ellipsis,
                    ),
                    selected: index == selectedChatIndex,
                    onTap: () {
                      setState(() {
                        selectedChatIndex = index;
                      });
                    },
                  );
                },
              ),
            ),
            Expanded(
              child: Column(
                children: [
                  Container(
                    padding: const EdgeInsets.all(16),
                    decoration: BoxDecoration(
                      color: Theme.of(context).cardColor,
                      border: Border(
                        bottom: BorderSide(color: Colors.grey.shade300),
                      ),
                    ),
                    child: Row(
                      children: [
                        CircleAvatar(
                          backgroundColor: Colors.grey.shade200,
                          radius: 24,
                          child: ClipOval(
                            child: Image.network(
                              selectedClient.avatarUrl,
                              width: 48,
                              height: 48,
                              fit: BoxFit.cover,
                              errorBuilder: (context, error, stackTrace) =>
                                  const Icon(Icons.person, size: 24),
                            ),
                          ),
                        ),
                        const SizedBox(width: 12),
                        Text(
                          selectedClient.name,
                          style: Theme.of(context).textTheme.titleMedium,
                        ),
                      ],
                    ),
                  ),
                  Expanded(
                    child: ListView.builder(
                      padding: const EdgeInsets.all(16),
                      itemCount: selectedClient.messages.length,
                      itemBuilder: (_, index) {
                        final msg = selectedClient.messages[index];
                        return Align(
                          alignment: msg.fromClient
                              ? Alignment.centerLeft
                              : Alignment.centerRight,
                          child: Container(
                            padding: const EdgeInsets.all(10),
                            margin: const EdgeInsets.symmetric(vertical: 4),
                            decoration: BoxDecoration(
                              color: msg.fromClient
                                  ? Colors.grey.shade200
                                  : ColorsConstants.primaryColor
                                      .withValues(alpha: 0.9),
                              borderRadius: BorderRadius.circular(8),
                            ),
                            child: Text(
                              msg.text,
                              style: TextStyle(
                                color: msg.fromClient
                                    ? Colors.black87
                                    : Colors.white,
                              ),
                            ),
                          ),
                        );
                      },
                    ),
                  ),
                  Container(
                    padding: const EdgeInsets.all(12),
                    decoration: BoxDecoration(
                      color: Theme.of(context).cardColor,
                      border: Border(
                        top: BorderSide(color: Colors.grey.shade300),
                      ),
                    ),
                    child: Row(
                      children: [
                        Expanded(
                          child: TextFormField(
                            controller: _msgController,
                            decoration: const InputDecoration(
                              hintText: 'Digite sua mensagem...',
                              border: OutlineInputBorder(),
                            ),
                          ),
                        ),
                        const SizedBox(width: 8),
                        IconButton(
                          onPressed: sendMessage,
                          icon: const Icon(Icons.send),
                          color: ColorsConstants.primaryColor,
                        ),
                      ],
                    ),
                  ),
                ],
              ),
            ),
          ],
        ));
  }
}
